import React, {Fragment} from 'react';
import style from './css/getstatsbutton.module.css'

const GetStatsButton = ({ playerId, getStats}) => {
    const onClick = () => {
        getStats(playerId);     
    }

    return (
        <Fragment>
          <p className={style.btn} onClick={onClick}>Get Stats &rarr;</p>     
        </Fragment>
    )
}

export default GetStatsButton
