import React, { useContext}  from 'react';
import './App.css';
import Search from './Search';
import axios from 'axios'


const App = ({ text }) => {

  const[players, setPlayers] = useContext([])

  const getPlayers = async (text) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${text}`);

    setPlayers(res.data)
  }

  return (
    <div className="App">
      <Search getPlayers={getPlayers}/>    
    </div>
  );
}

export default App;
