import React, { useState}  from 'react';
import './css/App.css';
import Search from './Search';
import PlayerInfo from './PlayerInfo';
import axios from 'axios';


const App = () => {
  const [player, setPlayer] = useState([]);
  const [playerId, setPlayerId] =useState('');
  const [stats, setStats] = useState([])

  // Get players by name 
  const getPlayers = async (input) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${input}`);
    console.log(res.data.data);
    setPlayer(res.data.data);
  };

  // get playerID 
  const getPlayerId = async (input) => {
    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${input}`);
    console.log(res.data.data[0].id);
    setPlayerId(res.data.data[0].id);
  }
  console.log(playerId);

  return (
    <div className="App">
      <Search getPlayers={getPlayers} getPlayerId={getPlayerId}/>
      <div className="results">
        <div className="info">
          {player.map(info => (
            <PlayerInfo  key ={info.first_name + info.last_name} first={info.first_name} last={info.last_name} position={info.position} heightF={info.height_feet} heightI={info.height_inches} weight={info.weight_pounds} team={info.team.abbreviation}/>
          ))}
        </div>
      </div>
    </div>

  );

}

export default App;
