import React, { useState}  from 'react';
import './App.css';
import Search from './Search';
import PlayerInfo from './PlayerInfo';
import axios from 'axios'


const App = () => {

  const [player, setPlayer] = useState([]);

  const getPlayers = async (text) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${text}`);
    //console.log(res.data.data);

    setPlayer(res.data.data);
  }

  return (
    <div className="App">
      <Search getPlayers={getPlayers}/>

      <h1></h1>
      <div className="info">
        {player.map(info => (
          <PlayerInfo />
        ))}
      </div>  
    </div>

  );
}

export default App;
