import React, { Fragment } from 'react'
import style from './css/playerInfo.module.css'
import GetStatsButton from './GetStatsButton'

const PlayerInfo = ({ first, last, position, heightF, heightI, weight, team, playerId, getStats, setBtn, btn}) => {

    const addButton = () => {
        setBtn(true);
        return (
            <GetStatsButton playerId={playerId} getStats={getStats}/> 
        )    
    }

    return (
        <div>
            <h1 className="heading">'19 - '20 season averages</h1>
            <div>
                <h2 className={style.playername}>{first} {last}</h2>
                <p className={style.playerinfo}>Position: {position} | Height: {heightF}"{heightI} | Weight: {weight}lbs | Team: {team}</p>
                 <Fragment/>   
            </div>
        </div>
    )
}

export default PlayerInfo
