import React, { useState}  from 'react';
import './App.css';
import Search from './Search';
import PlayerInfo from './PlayerInfo';
import axios from 'axios'


const App = () => {

  const [player, setPlayer] = useState([]);

  const getPlayers = async (text) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${text}`);
    console.log(res.data.data);

    setPlayer(res.data.data);
  }

  return (
    <div className="App">
      <Search getPlayers={getPlayers}/>
      <div className="results">
        <h1 className="heading">'19 - '20 season averages</h1>
        <div className="info">
          {player.map(info => (
            <PlayerInfo  first-name={info.first_name} last-name/>
          ))}
        </div>
      </div>
    </div>

  );
}

export default App;
