import React, { useState }  from 'react';
import './css/App.css';
import Search from './Search';
import PlayerInfo from './PlayerInfo';
import PlayerStats from './PlayerStats'
import axios from 'axios';


const App = () => {
  const [player, setPlayer] = useState([]);
  const [playerId, setPlayerId] =useState('');
  const [stats, setStats] = useState([]);


  // Get players by name 
  const getPlayers = async (input) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${input}`);
    //console.log(res.data.data);
    setPlayer(res.data.data);
  };


  // get playerID 
  const getPlayerId = async (input) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/players?search=${input}`);
    //console.log(res.data.data[0].id);
    setPlayerId(res.data.data[0].id);
  };


  // get player stats by ID 
  const getStats = async (playerId) => {

    const res = await axios.get(`https://www.balldontlie.io/api/v1/season_averages?season=2019&player_ids[]=${playerId}`);
    console.log(res.data.data[0]);
    setStats(res.data.data[0])

  };



  return (
    <div className="App">
      <Search getPlayers={getPlayers} getPlayerId={getPlayerId} playerId={playerId} getStats={getStats}/>
      <div className="results">
        <div className="info">
          {player.map(info => (
            <PlayerInfo  key ={info.first_name + info.last_name} first={info.first_name} last={info.last_name} position={info.position} heightF={info.height_feet} heightI={info.height_inches} weight={info.weight_pounds} team={info.team.abbreviation}/>
          ))}
          {stats.map(stat => (
            <PlayerStats key={stat.player_id}/>
          ))}
        </div>
      </div>
    </div>

  );

}

export default App;
